mapper.select:
		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_admin
        				 order by no desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}

		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_notice
        				 order by no desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}

		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_user
        				 order by reg_date desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}

		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_board
        				 where code_no = '3001'
        				 order by board_no desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}

		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_board
        				 where code_no = '3002'
        				 order by board_no desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}

		select X.*
		  from ( 
    		    select rownum as rnum, A.*
    			  from (
        		        select *
        				  from tb_scrap
        				 order by scrap_no desc
        			   ) A
                 where rownum <= #{endCount}
                ) X
         where X.rnum >= #{startCount}
